      k = 0;
      for (i = g_showStatement + 1; i <= g_statements; i++) {
        if (g_Statement[i].type == (char)p_ ||
            g_Statement[i].type == (char)a_) {
          j = i;
          break;
        }
      }
      if (j == 0) {
        k = 1; // Last statement flag
        // For the last statement, wrap to first one
        for (i = 1; i <= g_statements; i++) {
          if (g_Statement[i].type == (char)p_ ||
              g_Statement[i].type == (char)a_) {
            j = i;
            break;
          }
        }
      }
      if (j == 0) bug(2315);
